sqlMap.select:
SELECT * INTO ##temp_cfp FROM tpics_cust_fg_part;
SELECT *, COUNT(*) OVER (PARTITION BY 'A') AS cnt FROM( SELECT DISTINCT a.custcode, a.custname, a.fgno, a.fgname, a.partno, a.partname FROM ##temp_cfp a INNER JOIN (SELECT [cbom_fg],[cbom_level],[cbom_usedqty],[cbom_item],[cbom_oyak] ,[cbom_process], CASE [cbom_process] WHEN 'DIE CAST' THEN SUBSTRING([cbom_wc],1,4) ELSE [cbom_wc] END AS cbom_wc FROM tpics_cbom b WHERE b.cbom_process not in ('Purchase','Nominate')) b ON a.fgno = b.cbom_fg INNER JOIN tpics_xsect x ON x.bumo = b.cbom_wc AND (x.bunr = 'PROCESS' OR x.busho = 'Outsource')
WHERE NOT EXISTS (SELECT 1 FROM v_cust_fg_part p INNER JOIN m_part_wip pw on p.partid = pw.partid WHERE p.partno = a.partno and p.partname = a.partname and p.fgno = a.fgno and p.fgname = a.fgname and p.customerCode = a.custcode and pw.wip = x.bumo and pw.tpicsOrder = b.cbom_level) a.custcode = #customerCode# a.fgNo like #fgNo# + '%' a.fgName like #fgName# + '%' a.partNo like #partNo# + '%' a.partName like #partName# + '%' ) a ORDER BY a.custcode, a.fgno, a.partno; DROP TABLE ##temp_cfp;
