sqlMap.select:
SELECT tb.customercode ,tb.moldname ,tb.partname ,tb.partno ,tb.reportdate ,tb.moldid ,tb.moldno ,SUM(tb.totaldcshot) AS totaldcshot ,SUM(tb.totalfgsold) AS totalfgsold FROM ( SELECT v.customercode, m.moldname, v.partname, v.partno, h.reportdate, h.moldid, h.moldno,h.totaldcshot, 0 AS totalfgsold FROM t_mold_dc_history h
UNION ALL SELECT v.customercode, m.moldname, v.partname, v.partno, h.reportdate, h.moldid, h.moldno, 0 AS totaldcshot, case when h.totalfgsold > 0 then h.totalfgsold / d.qtyshot else h.totalfgsold end totalfgsold FROM t_mold_fg_history h
) tb GROUP BY tb.customercode, tb.moldname, tb.partname, tb.partno, tb.reportdate, tb.moldid, tb.moldno ORDER BY tb.reportdate desc, tb.customercode, tb.moldname, tb.moldno, tb.partno

SELECT SUM(c) AS c FROM ( select COUNT(*) AS c from t_mold_dc_history h
Union All select COUNT(*) AS c from t_mold_fg_history h
) tb
