sqlMap.select:
SELECT C_1.calendardate as reportDate ,C_1.fgid as fgId ,C_1.fgName ,C_1.fgNo ,ISNULL(FG_DEV.fgout,0) as actualQty ,del.balanceqty as balanceQty ,ISNULL(del.deliveryqty,0) as deliveryQty ,FG_DEV.fgbalance as fgBalance ,FG_DEV.fgin as fgIn ,FG_DEV.fgout as fgOut ,FG_DEV.fgstock as fgStock ,FG_DEV.fgadjust as fgAdjust ,day(C_1.calendardate) AS reportDay , del.forcastqty AS forcastQty FROM ( SELECT c.calendardate ,fg_1.fgid ,fg_1.fgno ,fg_1.fgname FROM m_calendar AS c CROSS JOIN ( SELECT fgid ,fgno ,fgname FROM v_cust_fg_part
customerid = #customerId# fgid = #fgId#
) AS fg_1
c.calendardate >= #reportDateFr# c.calendardate <= #reportDateTo#
) AS C_1 LEFT OUTER JOIN ( SELECT d.fgid ,d.deliverydate ,d.deliveryqtytotal AS deliveryqty ,d.balanceorderqty AS balanceqty ,d.forcastqty FROM t_deliveryplan_date AS d INNER JOIN t_deliveryplan AS p ON d.deliveryplanid = p.deliveryplanid
d.fgid = #fgId# p.customerid = #customerId# d.deliverydate >= #reportDateFr# d.deliverydate <= #reportDateTo#
AND p.revision = ( SELECT MAX(revision) AS Expr1 FROM t_deliveryplan
customerid = #customerId# yearmonth = #yearMonth#
) ) AS del ON C_1.calendardate = del.deliverydate AND C_1.fgid = del.fgid LEFT OUTER JOIN ( SELECT reportdate ,fgid ,fgbalance ,fgout ,fgin ,fgstock ,fgadjust FROM t_fg_stock AS f
customerid = #customerId# fgid = #fgId# reportdate >= #reportDateFr# reportdate <= #reportDateTo#
) AS FG_DEV ON C_1.calendardate = FG_DEV.reportdate AND C_1.fgid = FG_DEV.fgid
C_1.calendardate >= #reportDateFr# C_1.calendardate <= #reportDateTo#
ORDER BY fgno, C_1.calendardate

SELECT c_1.calendardate AS deliveryDate, c_1.fgid AS fgId, del.balanceqty AS balanceOrderQty, del.deliveryqty AS balanceDeliveryQty FROM (SELECT c.calendardate, fg_1.fgid FROM m_calendar AS c cross join (SELECT fgid FROM v_cust_fg_part
customerid = #customerId# fgid = #fgId#
) AS fg_1
c.calendardate >= #reportDateFr# c.calendardate <= #reportDateTo#
) AS c_1 LEFT OUTER JOIN (SELECT d.fgid, d.deliverydate, d.deliveryqtytotal AS deliveryqty, d.balanceorderqty AS balanceqty FROM t_deliveryplan_date AS d inner join t_deliveryplan AS p ON d.deliveryplanid = p.deliveryplanid
d.fgid = #fgId# d.deliverydate >= #reportDateFr# d.deliverydate <= #reportDateTo#
AND p.revision = (SELECT MAX(revision) AS Expr1 FROM t_deliveryplan
customerid = #customerId# yearmonth = #yearMonth#
)) AS del ON c_1.calendardate = del.deliverydate AND c_1.fgid = del.fgid ORDER BY c_1.calendardate
