insert.iterate:
UPDATE t_pending_rework SET reworkAdjustQty = ISNULL(reworkAdjustQty,0) + #adjustList[].ok# + #adjustList[].ng# 
	,isClosed = '1'
WHERE pdReworkId = #adjustList[].pdReworkId#;

INSERT INTO t_rework_adjust(pdReworkId, reportdate, ok, ng, createby, updateby) 
VALUES (#adjustList[].pdReworkId#, CONVERT(DATE,GETDATE()), #adjustList[].ok#, #adjustList[].ng#, #createBy#, #updateBy#);
